    public void draw(Graphics2D g) {
        int worldcol=0;
        int worldrow=0;
        GamePanel.cloud_speed-=0.3f;


        int count=0;
        while (worldcol<3000&&worldrow<1) {


            float worldx= worldcol*1370+GamePanel.cloud_speed;
            int worldy= worldrow*GamePanel.titlesize;
            float screenx=worldx- entity.worldx + Player.screenx+GamePanel.mountain_screenx_2;
            int screeny=worldy- entity.worldy + Player.screeny+GamePanel.mountain_screeny;
//                if(count<2) {
                count++;
//                } else if (count>=2) {
//                    count--;
//                }


            if (map[count]!=1 && map[count]!=2 && map[count]!=0) {


                switch (map[count]) {

                    case 3:
                        g.drawImage(GamePanel.images.get("Cloud_1"), (int) screenx, screeny, 1380, 300, null);
                        break;
                    case 4:
                        g.drawImage(GamePanel.images.get("Cloud_2"), (int) screenx, screeny, 1380, 300, null);
                        break;
                    case 5:
                        g.drawImage(GamePanel.images.get("Cloud_3"), (int) screenx, screeny, 1380, 300, null);
                        break;
                }//count sifirlanirsa sozuza kadar doner
                if (GamePanel.cloud_appear_time>4600) {
                    GamePanel.cloud_appear_time=0;
                    map[count]=0;
                    if(map[count+1]==5) {
                    map[count+2]=3;}
                    else if (map[count+1]==4) {
                        map[count+2]=5;
                    }else if (map[count+1]==3) {
                        map[count+2]=4;
                    }
     
                 }
            }

            worldcol++;
            
//                if (GamePanel.cloud_appear_time==3750) {
//                    GamePanel.cloud_appear_time=0;
//                    count
//                }

            if (worldcol==3000) {
                worldcol=0;
                worldrow++;



            }

        }



    }
